document.addEventListener("DOMContentLoaded",function(){console.log("js executed...")});window.addEventListener("load",function(){let e=document.querySelector("#nav-wrapper");document.querySelector("#primary-menu-toggle").addEventListener("click",function(n){n.preventDefault(),e.classList.toggle("show-menu")})});const o=document.querySelectorAll("#menu-main a"),t=document.querySelector(".current_page_item a");o.forEach(e=>{e.addEventListener("mouseover",function(){t.classList.add("!opacity-50")}),e.addEventListener("mouseout",function(){t.classList.remove("!opacity-50")})});
